insert into greeting (message) VALUES ('Добро пожаловать! Готовы увидеть, что скрывают книжные страницы?');
insert into greeting (message) VALUES ('Здравствуйте! Приготовьтесь к новому книжному приключению.');
insert into greeting (message) VALUES ('Рад видеть вас! Давайте вместе углубимся в мир литературы.');
